import React from 'react'
import Item from './Item';

const items = [
    {
        src:"bowl.png"
    },
    {
        src:"sandals.png"
    },
    {
        src:"rock.png"
    },
    {
        src:"cam.png"
    },
    {
        src:"light.png"
    },
    {
        src:"chair.png"
    }
]

export default function Scene() {
    return (
        <div className="scene">
            {
                items.map( (item, index) => {
                    const reference = React.createRef();
                    refs.push(reference);
                    return <Item item={item} ref={reference} key={index}/>
                })
            }
        </div>
    )
}
